<template>
  <view v-if="imgList" class="l-upload-img-box">
    <view class="single" v-if="single">
      <view class="img-box" v-for="(item, index) in imgList" :key="index" @click="handleClick(item, index)">
        <l-image class="img" :src="item.src || defaultImg" width="100%" height="400rpx" />
        <view class="l-assist l-text-margin">{{ item.text }}</view>
      </view>
    </view>
    <view class="vc multiple" v-else-if="multiple">
      <view class="img-box" v-for="(item, index) in imgList" :key="index" @click="handleClick(item, index)">
        <l-image class="img" :src="item.src || defaultImg" width="300rpx" height="300rpx" />
        <view class="l-assist l-text-margin">{{ item.text }}</view>
      </view>
    </view>
    <view class="vc scroll" v-else-if="scroll">
      <view class="img-box" v-for="(item, index) in imgList" :key="index" @click="handleClick(item, index)">
        <l-image class="img" :src="item.src || defaultImg" />
        <view class="l-assist l-text-margin">{{ item.text }}</view>
      </view>
    </view>
  </view>
</template>
<script>
/**
 *
 * @description 图片上传
 * @property {Array} imgList - 图片列表{src:图片地址,text:图片提示}
 * @property {Boolean} single - 单个
 * @property {Boolean} multiple - 多个
 * @property {Boolean} scroll - 多个带滑动
 * @property {Boolean} isClick - 是否允许点击,默认允许
 * @property {String} defaultImg - 默认图片
 * @event {Function} click - 点击事件
 */

export default {
  name: 'l-upload-img',
  props: {
    hint: {
      type: Object,
      default: () => ({}),
    },
    imgList: Array,
    single: Boolean,
    multiple: Boolean,
    scroll: Boolean,
    isClick: {
      type: Boolean,
      default: () => true,
    },
    defaultImg: {
      type: String,
      default: () =>
        'data:image/png;base64,',
    },
  },
  methods: {
    async handleClick(item, index) {
      this.isClick && this.$emit('click', item, index);
    },
  },
  components: {},
};
</script>

<style lang="scss" scoped>
@import '../../style/index.scss';
.l-upload-img-box {
  text-align: center;
}
.single {
  .img-box {
    width: 100%;
  }
}

.multiple {
  .img-box {
    $width: 300rpx;
    width: $width;
    margin-right: 40rpx;
  }
}
</style>
